function add_realty() {
    var x = document.getElementById('realty');
    if (x.checked) {
        $('#realty_item').addClass("border-icons");
    }
    else {
        $('#realty_item').removeClass("border-icons");
    }
}
function add_cars() {
    var x = document.getElementById('cars');
    if (x.checked) {
        $('#cars_item').addClass("border-icons");
    }
    else {
        $('#cars_item').removeClass("border-icons");
    }
}
function add_metall() {
    var x = document.getElementById('metalic');
    if (x.checked) {
        $('#horseshoe_item').addClass("border-icons");
    }
    else {
        $('#horseshoe_item').removeClass("border-icons");
    }
}
function subscribe() {
    var user = {};
    var surname, name, second_name;
    surname = document.getElementById('surname').value;
    name = document.getElementById('name').value;
    email = document.getElementById('email').value;
    var check_rent = document.getElementById('realty');
    var check_cars = document.getElementById('cars');
    var check_property = document.getElementById('metalic');
    var pattern = /^[a-zа-яё\s]+$/iu;
    var pattern_mail = /^[-._a-z0-9]+@(?:[a-z0-9][-a-z0-9]+\.)+[a-z]{2,6}$/
    if (!surname || !name || !email) {
        $('#answer').text('Заповніть, будь-ласка, всі поля');
    }

    else {
        if (pattern.test(surname) && pattern.test(name) && pattern_mail.test(email)) {


            if (check_cars.checked) {

                check_cars.value = true;

            }
            else {
                check_cars.value = false;
            }
            if (check_rent.checked) {
                check_rent.value = true;
            }
            else {
                check_rent.value = false;
            }
            if (check_property.checked) {
                check_property.value = true;
            }
            else {
                check_property.value = false;
            }
            if (!check_cars.checked && !check_rent.checked && !check_property.checked) {
                check_cars.value = true;
                check_rent.value = true;
                check_property.value = true;
            }
            $('#answer').text('Ви успішно підписались на новини');

            jQuery.post("https://tender.uub.com.ua/DataHandler.ashx?Com"
                + "&surname=" + encodeURIComponent(surname)
                + "&name=" + encodeURIComponent(name)
                + "&u_pass=" + encodeURIComponent(second_name)
                + "&email=" + encodeURIComponent(email)
                + "&rent=" + encodeURIComponent(check_rent.value)
                + "&cars=" + encodeURIComponent(check_cars.value)
                + "&metall=" + encodeURIComponent(check_property.value)
                , HandleRegister);
        }
        else if (!pattern.test(surname)) {

            $('#answer').text("Перевірьте правильність вводу прізвища");

        }
        else if (!pattern.test(name)) {
            $('#answer').text("Перевірьте правильність вводу ім'я");
        }
        else if (!pattern_mail.test(email)) {
            $('#answer').text('Адреса електронної пошти введена некоректно');

        }


    }
}
